import java.util.*;public class CoinChangeProblem {    public static void main(String[] args) {        int[] coins = new int[]{9, 6, 5, 1, 7, 20};        int V = 79;        System.out.println(minimumCoin(coins, V));    }    public static int minimumCoin(int[] coins, int V) {        int sub_res = 0;        int[] table = new int[V+1];        Arrays.fill(table, Integer.MAX_VALUE);        table[0] = 0;        for(int i = 1; i <= V; ++i) {            for(int j = 0; j < coins.length; ++j){                if(coins[j] <= i) {                    sub_res = table[i - coins[j]];                    if(sub_res != Integer.MAX_VALUE && (1+sub_res) < table[i]) {                        table[i] = 1 + sub_res;                    }                }            }        }        return table[V];    }}